// src/pages/dashboard/PatientDashboard.jsx
import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import DashCard from "../../components/DashCard";
import { fetchPatientSummary } from "../../services/dashboard";

export default function PatientDashboard({ unread }) {
  const user = useSelector((s) => s.auth.user);

  const [sum, setSum] = useState({
    visits: 0,
    labResultsReady: 0,
    unreadNoti: unread,  // ← DÙNG PROP
    unpaidInvoices: 0,
    nextAppointment: null,
  });

  const [loading, setLoading] = useState(true);

  // Load data khác như bình thường (trừ unreadNoti)
  useEffect(() => {
  (async () => {
    try {
      const res = await fetchPatientSummary();
      setSum((prev) => ({ ...prev, ...res }));
    } catch {
      setSum({
        visits: 4,
        labResultsReady: 1,
        unpaidInvoices: 0,
        nextAppointment: {
          time: "10:30 - 21/11",
          clinic: "Tai–Mũi–Họng",
          status: "Đang chờ",
        },
      });
    } finally {
      setLoading(false);
    }
  })();
}, []);

// Cập nhật unread từ props
useEffect(() => {
  setSum((prev) => ({ ...prev, unreadNoti: unread }));
}, [unread]);

  return (
    <div className="auth-card" style={{ maxWidth: 1024 }}>
      <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
        <h2 style={{ margin: 0, flex: 1 }}>Patient Dashboard</h2>

        <Link to="/profile" className="chip-btn">
          Hồ sơ cá nhân
        </Link>
      </div>

      <p className="muted">
        Xin chào, {user?.username}. Đây là tổng quan sức khỏe của bạn.
      </p>

      {/* Cards */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
          gap: 16,
          marginTop: 16,
        }}
      >
        <DashCard
          title="Lịch sử khám bệnh"
          value={sum.visits}
          sub="Xem lịch sử khám & chi tiết (US2)"
          to="/visits"
        />

        <DashCard
          title="Kết quả xét nghiệm mới"
          value={sum.labResultsReady}
          sub="Xem / tải PDF (US3)"
          to="/lab-results"
        />

        <DashCard
          title="Thông báo chưa đọc"
          value={sum.unreadNoti}
          sub="Thông báo tự động (US5/US7)"
          to="/autonotifications"
        />

        <DashCard
          title="Hóa đơn chưa thanh toán"
          value={sum.unpaidInvoices}
          sub="Thanh toán online (US6)"
          to="/billing"
        />
      </div>

      {/* Next appointment */}
      <div
        style={{
          marginTop: 24,
          background: "#0f1422",
          border: "1px solid #223",
          borderRadius: 16,
          padding: 16,
        }}
      >
        <div style={{ fontWeight: 600, marginBottom: 8 }}>
          Lịch khám sắp tới (US1 / US4)
        </div>

        {loading ? (
          <div className="muted">Đang tải…</div>
        ) : sum.nextAppointment ? (
          <div>
            <div><b>Thời gian:</b> {sum.nextAppointment.time}</div>
            <div><b>Phòng:</b> {sum.nextAppointment.clinic}</div>
            <div><b>Trạng thái:</b> {sum.nextAppointment.status}</div>

            <div style={{ marginTop: 10 }}>
              <Link to="/process-tracking" className="link">
                Xem trạng thái quy trình khám
              </Link>
            </div>
          </div>
        ) : (
          <div className="muted">Chưa có lịch khám.</div>
        )}
      </div>

      <div style={{ marginTop: 18 }}>
        <Link to="/chat" className="link">Nhắn tin với bác sĩ (US8)</Link>
      </div>
      <div style={{ marginTop: 18 }}>
        <Link to="/notifications" className="link">Thông báo chung từ bệnh viện (US7)</Link>
      </div>
      <div style={{ marginTop: 18 }}>
        <Link to="/chat" className="link">Nhắn tin với bác sĩ (US8)</Link>
      </div>
    </div>
  );
}
